import React, { Component } from 'react';
import {choice} from './Helper';
import CoinImg from './CoinImg';

export default class FlipCoin extends Component {
     static defaultProps = {
        coins:[
             { side:'head' , imgSrc:'data:image/jpeg;base64,' },
             { side:'tails' , imgSrc:'data:image/jpeg;base64,' }
         ]
     }

    constructor(props){
        super(props);
  

      this.state = {
          currentFlips :null,
          totalFlips:0,
          headFlips:0,
          tailsFlips:0
      };
     // this.handleClick = this.handleClick.bind(this);
    }

    //   flipCoin = () => {
    //       const newCoin = choice(this.props.coins);
    //     this.setState(st => {
    //         return {
    //             currentFlips :newCoin,
    //             totalFlips : st.totalFlips + 1
    //         }
          
    //     })

    //   }

      handleClick = () => {
        const newCoin = choice(this.props.coins);
        this.setState(st => {
            return {
                currentFlips :newCoin,
                totalFlips : st.totalFlips + 1,
                headFlips : st.headFlips + (newCoin.side === 'head' ? 1: 0),
                tailsFlips :st.tailsFlips + (newCoin.side === 'tails' ? 1 :0 )
            }
          
        })
      }

    
    render() {
      
        return (
            <div className='Coin-container'>
                <h2>Exercise 2</h2>
                <h1>Let's flip a coin</h1>
               
                { this.state.currentFlips &&  <CoinImg info={this.state.currentFlips} />}
                <button onClick={this.handleClick} >Flip the coin!</button>
                <p> Out of <b  style={{fontSize:'20px'}} > {this.state.totalFlips} </b> flips , there have been <b style={{color:'crimson',fontSize:'20px'}} > {this.state.headFlips} </b> 
                 {' '}  heads  and <b style={{color:'blue',fontSize:'20px'}} > {this.state.tailsFlips}   </b>tails
                </p>

            </div>
        )
    }
}
